settings.custom_liquid:
{% if product.metafields.custom.labels.value contains 'farben' %}
  <a href="#" class="tooltip">
    <img src="{{ 'ICON-Farben.png' | file_img_url }}" class="label-ico" />
    <span class="tooltiptext">Wenn dieses Label auf deinem Wunschdesign ist kannst du die Farbe passend zu deinem Geschmack bestimmen.</span>
  </a>
{% endif %}
{% if product.metafields.custom.labels.value contains 'variabel' %}
  <a href="#" class="tooltip">
    <img src="{{ 'ICON-Variabel.png' | file_img_url }}" class="label-ico" />
    <span class="tooltiptext">Bei allen Motiven mit diesem Label wird jedes Element einzeln von mir neu positioniert. Dadurch werden keine Elemente durch Ecken und Türen abgeschnitten.</span>
  </a>
{% endif %}

<script>
  function positionTooltip(tooltip, tooltipText) {
    tooltipText.style.bottom = '150%';
    tooltipText.style.left = '50%';
    tooltipText.style.marginLeft = '-60px';
    tooltipText.style.marginTop = '';

    const rect = tooltip.getBoundingClientRect();
    const winWidth = window.innerWidth;
    const winHeight = window.innerHeight;
    const spaceLeft = rect.left;
    const spaceRight = winWidth - rect.right;
    const spaceTop = rect.top;
    const spaceBottom = winHeight - rect.bottom;

    if (spaceTop < tooltipText.offsetHeight) {
      tooltipText.style.bottom = 'auto';
      tooltipText.style.top = '100%';
    }

    if (tooltipText.offsetWidth > Math.max(spaceLeft, spaceRight)) {
      tooltipText.style.left = '50%';
      tooltipText.style.marginLeft = -(tooltipText.offsetWidth / 2) + 'px';
    } else if (spaceRight < tooltipText.offsetWidth) {
      tooltipText.style.left = 'auto';
      tooltipText.style.right = '100%';
      tooltipText.style.marginLeft = '';
    }
  }

  document.addEventListener('DOMContentLoaded', function() {
    var tooltips = document.querySelectorAll('.tooltip');
    tooltips.forEach(function(tooltip) {
      var tooltipText = tooltip.querySelector('.tooltiptext');
      positionTooltip(tooltip, tooltipText);

      tooltip.addEventListener('click', function(event) {
        event.preventDefault();
        tooltipText.style.visibility = tooltipText.style.visibility === 'visible' ? 'hidden' : 'visible';
        positionTooltip(tooltip, tooltipText);
      });

      window.addEventListener('resize', function() {
        positionTooltip(tooltip, tooltipText);
      });
    });
  });
</script>
